import React, { Component } from 'react'
import OwnerServices from '../service/owner-services'


class RestaurantForm extends Component {

    constructor() {
        super()

        this.state = {

            restaurant: {
                name: '',
                adress: '',
                phone: '',
                logo: '',
                tables: '',
                description: '',

            },

            show: false
        }

        this.services = new OwnerServices()
    }

    handlechange = e => {
        const { name, value } = e.target
        this.setState({
            restaurant: {
                ...this.state.restaurant,
                [name]: value
            }
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        console.log('Formulario de Restaurante')
        // this.services.postRestaurant(this.state.restaurant)
        //     .then(x => window.location.href = "/coasters")
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label htmlFor="name">Name</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.name} type="text" id="name" name="name" />

                    <label htmlFor="name">Adress</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.adress} type="text" id="adress" name="adress" />

                    <label htmlFor="name">Phone</label>
                    <input onChange={this.hannamedlechange} value={this.state.restaurant.phone} type="number" id="phone" name="phone" />

                    <label htmlFor="logo">logo</label>
                    <input onChange={this.handleFileUpload} type="file" id="logo" name="logo" />

                    <label htmlFor="table_quantity">Table quantity</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.tables} type="number" id="tables" name="tables" />

                    <label htmlFor="name">Description</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.description} type="text" id="description" name="description" />

                    <button type="submit">Enviar</button>
                </form>
            </div>
        )
    }
}


export default RestaurantForm
