import React, { Component } from 'react'
import OwnerServices from '../service/owner-services'


class RestaurantForm extends Component {

    constructor() {
        super()

        this.state = {

            restaurant: {
                name: '',
                background: {
                    color: '',
                    image: {
                        imgName: '',
                        imgPath: '',
                    }
                },
                logo: {
                    imgName: '',
                    imgPath: '',
                },
                table: [
                    {
                        table_id: ''
                    }
                ]
            },

            show: false
        }

        this.services = new OwnerServices()
    }

    handlechange = e => {
        const { name, value } = e.target
        this.setState({
            coaster: {
                ...this.state.coaster,
                [name]: value
            }
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        console.log('Formulario de Restaurante')
        // this.services.postRestaurant(this.state.restaurant)
        //     .then(x => window.location.href = "/coasters")
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label htmlFor="nombre">Nombre</label>
                        <input onChange={this.handlechange} value={this.state.restaurant.name} type="text" id="nombre" name="nombre" />
                    </div>
                    <button type="submit" className="btn btn-dark">Enviar</button>
                </form>
            </div>
        )
    }
}


export default RestaurantForm
