import React, { Component } from 'react'
import OwnerServices from '../service/owner-services'


class RestaurantForm extends Component {

    constructor() {
        super()

        this.state = {

            restaurant: {
                name: '',
                background: {
                    color: '',
                    image: {
                        imgName: '',
                        imgPath: '',
                    }
                },
                logo: {
                    imgName: '',
                    imgPath: '',
                },
                table: {
                    quantity: '',
                    table_info: [
                        {
                            table_id: '',
                            qr_url: '',
                            qr_image: {
                                imgName: '',
                                imgPath: ''
                            }
                        }
                    ]
                }
            },

            show: false
        }

        this.services = new OwnerServices()
    }

    handlechange = e => {
        const { name, value } = e.target
        this.setState({
            restaurant: {
                ...this.state.restaurant,
                [name]: value
            }
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        console.log('Formulario de Restaurante')
        // this.services.postRestaurant(this.state.restaurant)
        //     .then(x => window.location.href = "/coasters")
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label htmlFor="name">Nombre</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.name} type="text" id="name" name="name" />

                    <label htmlFor="tables_quantity">Numero de mesas</label>
                    <input onChange={this.handlechange} value={this.state.restaurant.table.tables_quantity} type="number" id="tables_quantity" name="tables_quantity" />

                    <button type="submit">Enviar</button>
                </form>
            </div>
        )
    }
}


export default RestaurantForm
